const html = `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Jobs Selection</title>
  <style>
    :root {
      --bg: #0f1220;
      --card: #171a2b;
      --muted: #9aa4bf;
      --text: #eef1ff;
      --accent: #f4b266;
      --accent-2: #6fc3df;
      --border: #2a2f45;
      --chip: #1f2438;
      --shadow: 0 12px 30px rgba(0,0,0,0.35);
    }
    * { box-sizing: border-box; }
    body {
      margin: 0;
      font-family: "Space Grotesk", "Satoshi", "Segoe UI", sans-serif;
      background: radial-gradient(1200px 600px at 10% -20%, #243b55 0%, transparent 60%),
                  radial-gradient(800px 500px at 90% -10%, #3b2f2f 0%, transparent 60%),
                  var(--bg);
      color: var(--text);
    }
    header {
      padding: 28px 28px 12px;
      display: flex;
      align-items: flex-end;
      justify-content: space-between;
      gap: 16px;
    }
    h1 {
      margin: 0;
      font-size: 28px;
      letter-spacing: 0.2px;
    }
    .sub {
      color: var(--muted);
      font-size: 14px;
      margin-top: 6px;
    }
    .stats {
      display: flex;
      gap: 12px;
      flex-wrap: wrap;
    }
    .chip {
      background: var(--chip);
      border: 1px solid var(--border);
      border-radius: 999px;
      padding: 6px 12px;
      font-size: 12px;
      color: var(--muted);
    }
    .container {
      padding: 0 24px 32px;
      display: grid;
      grid-template-columns: 1fr;
      gap: 16px;
    }
    .panel {
      background: var(--card);
      border: 1px solid var(--border);
      border-radius: 14px;
      box-shadow: var(--shadow);
      padding: 16px;
    }
    .filters {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
      gap: 12px;
    }
    input, select, button, textarea {
      font: inherit;
      color: var(--text);
    }
    input, select {
      background: #111427;
      border: 1px solid var(--border);
      border-radius: 10px;
      padding: 10px 12px;
      width: 100%;
      outline: none;
    }
    button {
      background: linear-gradient(135deg, var(--accent), #f7d199);
      border: none;
      color: #1b1b1b;
      padding: 10px 14px;
      border-radius: 10px;
      cursor: pointer;
      font-weight: 600;
    }
    button.secondary {
      background: transparent;
      border: 1px solid var(--border);
      color: var(--text);
    }
    .table-wrap {
      overflow: auto;
      border-radius: 12px;
      border: 1px solid var(--border);
      background: #101424;
    }
    table {
      width: 100%;
      border-collapse: collapse;
      min-width: 720px;
    }
    thead th {
      position: sticky;
      top: 0;
      background: #12162b;
      text-align: left;
      font-size: 12px;
      text-transform: uppercase;
      letter-spacing: 0.08em;
      padding: 12px;
      border-bottom: 1px solid var(--border);
    }
    tbody td {
      padding: 12px;
      border-bottom: 1px solid var(--border);
      font-size: 14px;
    }
    tbody tr:hover {
      background: rgba(255,255,255,0.03);
    }
    .row-title {
      font-weight: 600;
      margin-bottom: 4px;
    }
    .row-meta {
      color: var(--muted);
      font-size: 12px;
    }
    .badge {
      display: inline-block;
      padding: 4px 8px;
      border-radius: 999px;
      background: rgba(111,195,223,0.15);
      color: var(--accent-2);
      font-size: 11px;
      border: 1px solid rgba(111,195,223,0.3);
    }
    .footer-bar {
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 12px;
      flex-wrap: wrap;
    }
    .selected-list {
      max-height: 120px;
      overflow: auto;
      border: 1px dashed var(--border);
      padding: 8px;
      border-radius: 10px;
      background: #0f1220;
      font-size: 12px;
      color: var(--muted);
    }
    .action-row {
      display: flex;
      gap: 10px;
      flex-wrap: wrap;
      align-items: center;
    }
    .pagination {
      display: flex;
      gap: 8px;
      align-items: center;
      color: var(--muted);
      font-size: 13px;
    }
    .empty {
      text-align: center;
      padding: 40px;
      color: var(--muted);
    }
    @media (max-width: 780px) {
      header {
        flex-direction: column;
        align-items: flex-start;
      }
    }
  </style>
</head>
<body>
  <header>
    <div>
      <h1>Job Selection Console</h1>
      <div class="sub">Pick jobs to advance in the workflow. Latest records are loaded from Supabase.</div>
    </div>
    <div class="stats">
      <div class="chip" id="total-count">0 jobs</div>
      <div class="chip" id="filtered-count">0 shown</div>
      <div class="chip" id="selected-count">0 selected</div>
    </div>
  </header>

  <div class="container">
    <div class="panel">
      <div class="filters">
        <input id="search" type="text" placeholder="Search title, company, location..." />
        <select id="filter-remote">
          <option value="">Remote + On-site</option>
          <option value="remote">Remote only</option>
          <option value="onsite">On-site only</option>
        </select>
        <input id="filter-type" type="text" placeholder="Employment type (e.g., Full-time)" />
        <select id="sort">
          <option value="recent">Sort by recent</option>
          <option value="company">Sort by company</option>
          <option value="title">Sort by title</option>
        </select>
      </div>
    </div>

    <div class="panel">
      <div class="table-wrap">
        <table>
          <thead>
            <tr>
              <th style="width: 48px;"><input id="toggle-all" type="checkbox" /></th>
              <th>Role</th>
              <th>Company</th>
              <th>Location</th>
              <th>Type</th>
              <th>Remote</th>
              <th>Posted</th>
              <th>Link</th>
            </tr>
          </thead>
          <tbody id="table-body"></tbody>
        </table>
        <div id="empty" class="empty" style="display:none;">No jobs match the current filters.</div>
      </div>
    </div>

    <div class="panel">
      <div class="footer-bar">
        <div class="pagination">
          <button class="secondary" id="prev-page">Prev</button>
          <span id="page-status">Page 1</span>
          <button class="secondary" id="next-page">Next</button>
        </div>
        <div class="action-row">
          <button class="secondary" id="clear-selection">Clear selection</button>
          <button id="submit-selection">Push selected to flow</button>
        </div>
      </div>
      <div style="margin-top: 12px;">
        <div class="sub" style="margin-bottom: 6px;">Selected jobs</div>
        <div id="selected-list" class="selected-list"></div>
      </div>
    </div>
  </div>

  <script>
    const state = {
      page: 1,
      perPage: 12,
      search: '',
      remote: '',
      type: '',
      sort: 'recent',
      selected: new Set(),
      jobs: [],
      total: 0,
      loading: false,
    };

    const el = (id) => document.getElementById(id);
    const tableBody = el('table-body');
    const empty = el('empty');
    const totalCount = el('total-count');
    const filteredCount = el('filtered-count');
    const selectedCount = el('selected-count');
    const selectedList = el('selected-list');

    const formatDate = (value) => {
      if (!value) return 'N/A';
      const d = new Date(value);
      if (Number.isNaN(d.getTime())) return value;
      return d.toLocaleDateString(undefined, { year: 'numeric', month: 'short', day: 'numeric' });
    };

    const getBase = () => {
      const useTest = window.location.pathname.includes('/webhook-test/');
      return window.location.origin + (useTest ? '/webhook-test/' : '/webhook/');
    };

    const loadData = async () => {
      state.loading = true;
      const params = new URLSearchParams({
        q: state.search || '',
        remote: state.remote || '',
        type: state.type || '',
        sort: state.sort || 'recent',
        page: String(state.page),
        perPage: String(state.perPage),
      });

      const url = \`\${getBase()}jobs-ui-data?\${params}\`;
      try {
        const res = await fetch(url);
        if (!res.ok) throw new Error(\`HTTP \${res.status}\`);
        const payload = await res.json();
        state.jobs = Array.isArray(payload.items) ? payload.items : [];
        state.total = Number(payload.total || payload.totalCount || state.jobs.length || 0);
      } catch (err) {
        console.error(err);
        state.jobs = [];
        state.total = 0;
      } finally {
        state.loading = false;
        render();
      }
    };

    const render = () => {
      const totalPages = Math.max(1, Math.ceil(state.total / state.perPage) || 1);
      state.page = Math.min(state.page, totalPages);

      totalCount.textContent = \`\${state.total} jobs\`;
      filteredCount.textContent = \`\${state.jobs.length} shown\`;
      selectedCount.textContent = \`\${state.selected.size} selected\`;

      tableBody.innerHTML = '';
      if (!state.jobs.length) {
        empty.style.display = 'block';
      } else {
        empty.style.display = 'none';
      }

      state.jobs.forEach((job) => {
        const tr = document.createElement('tr');
        const checked = state.selected.has(job.id);
        tr.innerHTML = \`
          <td><input type="checkbox" data-id="\${job.id}" \${checked ? 'checked' : ''} /></td>
          <td><div class="row-title">\${job.title}</div><div class="row-meta">\${job.id}</div></td>
          <td>\${job.company}</td>
          <td>\${job.location}</td>
          <td>\${job.employmentType || 'N/A'}</td>
          <td>\${job.remote ? '<span class="badge">Remote</span>' : 'On-site'}</td>
          <td>\${formatDate(job.postedAt || job.createdAt)}</td>
          <td>\${job.url ? \`<a href="\${job.url}" target="_blank" rel="noopener" style="color: var(--accent-2);">Open</a>\` : 'N/A'}</td>
        \`;
        tableBody.appendChild(tr);
      });

      el('page-status').textContent = \`Page \${state.page} of \${totalPages}\`;
      renderSelectedList();
    };

    const renderSelectedList = () => {
      if (!state.selected.size) {
        selectedList.textContent = 'No jobs selected yet.';
        return;
      }
      const selectedJobs = state.jobs.filter(j => state.selected.has(j.id));
      if (!selectedJobs.length) {
        selectedList.textContent = \`\${state.selected.size} jobs selected across pages.\`;
        return;
      }
      selectedList.innerHTML = selectedJobs.map(j => \`\${j.title} â€” \${j.company}\`).join('<br />');
    };

    const debounce = (fn, delay) => {
      let t;
      return (...args) => {
        clearTimeout(t);
        t = setTimeout(() => fn(...args), delay);
      };
    };

    tableBody.addEventListener('change', (event) => {
      const target = event.target;
      if (target && target.matches('input[type="checkbox"][data-id]')) {
        const id = target.getAttribute('data-id');
        if (target.checked) state.selected.add(id);
        else state.selected.delete(id);
        selectedCount.textContent = \`\${state.selected.size} selected\`;
        renderSelectedList();
      }
    });

    el('toggle-all').addEventListener('change', (event) => {
      const checked = event.target.checked;
      state.jobs.forEach((job) => {
        if (checked) state.selected.add(job.id);
        else state.selected.delete(job.id);
      });
      render();
    });

    el('search').addEventListener('input', debounce((e) => {
      state.search = e.target.value.trim();
      state.page = 1;
      loadData();
    }, 300));

    el('filter-remote').addEventListener('change', (e) => {
      state.remote = e.target.value;
      state.page = 1;
      loadData();
    });

    el('filter-type').addEventListener('input', debounce((e) => {
      state.type = e.target.value.trim();
      state.page = 1;
      loadData();
    }, 300));

    el('sort').addEventListener('change', (e) => {
      state.sort = e.target.value;
      state.page = 1;
      loadData();
    });

    el('prev-page').addEventListener('click', () => {
      state.page = Math.max(1, state.page - 1);
      loadData();
    });

    el('next-page').addEventListener('click', () => {
      const maxPage = Math.max(1, Math.ceil(state.total / state.perPage));
      state.page = Math.min(maxPage, state.page + 1);
      loadData();
    });

    el('clear-selection').addEventListener('click', () => {
      state.selected.clear();
      render();
    });

    el('submit-selection').addEventListener('click', async () => {
      if (!state.selected.size) {
        alert('Select at least one job to continue.');
        return;
      }
      const base = getBase();
      const url = base + 'jobs-ui-submit';
      const payload = { selectedJobIds: Array.from(state.selected) };

      try {
        const res = await fetch(url, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(payload),
        });
        if (!res.ok) throw new Error(\`HTTP \${res.status}\`);
        const html = await res.text();
        document.open();
        document.write(html);
        document.close();
      } catch (err) {
        alert('Submit failed. Check n8n logs and try again.');
        console.error(err);
      }
    });

    loadData();
  </script>
</body>
</html>`;

return [{ json: { html } }];

const query = $json.query || $json || {};
const search = String(query.q || '').trim();
const sort = String(query.sort || 'recent').trim();
const page = Math.max(1, parseInt(query.page || '1', 10) || 1);
const perPage = Math.min(50, Math.max(5, parseInt(query.perPage || '12', 10) || 12));
const offset = (page - 1) * perPage;

let where = '1=1';
if (search) {
  const safe = search.replace(/'/g, "\\'");
  where += ` AND (job_title LIKE '%${safe}%' OR employer_name LIKE '%${safe}%' OR job_location LIKE '%${safe}%')`;
}
if (query.remote === 'remote') where += " AND job_is_remote = 1";
if (query.remote === 'onsite') where += " AND job_is_remote = 0";
if (query.type) {
  const safeType = query.type.replace(/'/g, "\\'");
  where += ` AND job_employment_type LIKE '%${safeType}%'`;
}

let orderBy = 'job_posted_at_datetime_utc DESC';
if (sort === 'company') orderBy = 'employer_name ASC';
if (sort === 'title') orderBy = 'job_title ASC';

const sql = `SELECT * FROM jobs WHERE ${where} ORDER BY ${orderBy} LIMIT ${perPage} OFFSET ${offset}`;
return [{ json: { query: sql, page, perPage } }];

// Get all items from the MySQL node
const response = $input.all().map(item => item.json);
const total = response.length;

const items = response.map((j) => ({
  id: String(j.job_id || j.id || ''),
  title: j.job_title || 'Untitled role',
  company: j.employer_name || 'Unknown company',
  location: j.job_location || [j.job_city, j.job_state].filter(Boolean).join(', ') || 'Location N/A',
  country: j.job_country || '',
  employmentType: j.job_employment_type || (Array.isArray(j.job_employment_types) ? j.job_employment_types.join(', ') : ''),
  remote: Boolean(j.job_is_remote),
  postedAt: j.job_posted_at_datetime_utc || j.job_posted_at || '',
  url: j.job_apply_link || j.job_google_link || '',
  createdAt: j.created_at || ''
}));

const meta = $items('Build Jobs Data Query')[0]?.json || {};
return [{ json: { items, total, page: meta.page || 1, perPage: meta.perPage || 12 } }];

const payload = $json.body || $json;
let selectedIds = payload.selectedJobIds || payload.selectedIds || payload.ids || [];
if (typeof selectedIds === 'string') {
  try { selectedIds = JSON.parse(selectedIds); } catch (e) { selectedIds = selectedIds.split(','); }
}
const selectedList = Array.isArray(selectedIds) ? selectedIds : [selectedIds];
const ids = selectedList.map(id => `'${String(id).replace(/'/g, "\\'")}'`).join(',');

if (!ids) return [{ json: { query: "SELECT * FROM jobs WHERE 1=0" } }];
return [{ json: { query: `SELECT * FROM jobs WHERE job_id IN (${ids})` } }];

return $input.all().map(item => {
  const j = item.json;
  return {
    json: {
      job_id: j.job_id,
      job_title: j.job_title,
      employer_name: j.employer_name,
      job_location: j.job_location,
      job_country: j.job_country,
      job_employment_type: j.job_employment_type,
      job_is_remote: j.job_is_remote ? 1 : 0,
      job_posted_at_datetime_utc: j.job_posted_at_datetime_utc ? new Date(j.job_posted_at_datetime_utc).toISOString().slice(0, 19).replace('T', ' ') : null,
      job_apply_link: j.job_apply_link,
      job_google_link: j.job_google_link
    }
  };
});

