return [
  { json: { keyword: "GTM Engineer" } },
  { json: { keyword: "Go To Market Engineer" } },
  { json: { keyword: "Clay GTM" } }
];

const data = $items("Save People to DB")[0].json;
const allPeople = data.allPeople || [];
const companiesSearched = data.companiesSearched || 0;
const totalPeople = data.totalPeople || 0;
const searchBatchId = data.searchBatchId || '';

const esc = (s) => String(s || '').replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;').replace(/"/g,'&quot;');

// Group people by company, suggested first
const grouped = {};
for (const p of allPeople) {
  if (!grouped[p.company]) grouped[p.company] = { suggested: [], others: [] };
  if (p.is_suggested) {
    grouped[p.company].suggested.push(p);
  } else {
    grouped[p.company].others.push(p);
  }
}

const suggestedCount = allPeople.filter(p => p.is_suggested).length;
const companies = Object.keys(grouped);

let peopleHtml = '';
let compIdx = 0;
for (const companyName of companies) {
  const grp = grouped[companyName];
  const all = [...grp.suggested, ...grp.others];
  const sj = all[0] || {};

  peopleHtml += '<div class="company-section" data-company="' + compIdx + '">';
  peopleHtml += '<div class="company-header">';
  peopleHtml += '<div class="company-header-left">';
  peopleHtml += '<label class="cb-wrap"><input type="checkbox" class="company-select-all" data-company="' + compIdx + '" /><span class="checkmark"></span></label>';
  peopleHtml += '<div><div class="company-name">' + esc(companyName) + '</div>';
  peopleHtml += '<div class="company-meta">' + all.length + ' people found';
  if (grp.suggested.length > 0) peopleHtml += ' &middot; ' + grp.suggested.length + ' AI suggested';
  if (sj.sourceJobTitle) peopleHtml += ' &middot; Source: ' + esc(sj.sourceJobTitle);
  peopleHtml += '</div></div></div></div>';

  for (const p of all) {
    const skillsHtml = (p.skills || []).slice(0, 6).map(s => '<span class="skill">' + esc(s) + '</span>').join('');
    const expHtml = (p.experience || []).slice(0, 2).map(e => '<div class="exp-item">' + esc(e) + '</div>').join('');
    const otwBadge = p.openToWork ? '<span class="badge otw">Open to work</span>' : '';
    const sugBadge = p.is_suggested ? '<span class="badge ai-sug">AI Suggested</span>' : '';
    const reasonHtml = p.is_suggested && p.suggestion_reason ? '<div class="sug-reason">' + esc(p.suggestion_reason) + '</div>' : '';

    peopleHtml += '<div class="person-row' + (p.is_suggested ? ' suggested' : '') + '">';
    peopleHtml += '<label class="cb-wrap"><input type="checkbox" class="person-cb" data-company="' + compIdx + '" data-uid="' + esc(p.uid) + '" /><span class="checkmark"></span></label>';
    peopleHtml += '<div class="person-info">';
    peopleHtml += '<div class="person-name-line"><span class="person-name">' + esc(p.fullName) + '</span>' + sugBadge + otwBadge + '</div>';
    peopleHtml += '<div class="person-title">' + esc(p.currentTitle) + '</div>';
    peopleHtml += '<div class="person-location">' + esc(p.location) + '</div>';
    peopleHtml += reasonHtml;
    peopleHtml += '<div class="person-skills">' + (skillsHtml || '') + '</div>';
    peopleHtml += '<div class="person-exp">' + (expHtml || '') + '</div>';
    peopleHtml += '</div></div>';
  }

  peopleHtml += '</div>';
  compIdx++;
}

const html = `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8"/>
  <meta name="viewport" content="width=device-width, initial-scale=1"/>
  <title>People Selection - Enrichment</title>
  <link rel="preconnect" href="https://fonts.googleapis.com">
  <link href="https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@400;500;600;700&display=swap" rel="stylesheet">
  <style>
    :root{--bg:#0f1220;--card:#171a2b;--muted:#9aa4bf;--text:#eef1ff;--accent:#f4b266;--accent-2:#6fc3df;--border:#2a2f45;--chip:#1f2438;--shadow:0 12px 30px rgba(0,0,0,.35);--green:#4ade80;--purple:#a78bfa;--gold:#f4b266}
    *{box-sizing:border-box;margin:0}

    body{font-family:"Space Grotesk","Segoe UI",sans-serif;background:radial-gradient(1200px 600px at 10% -20%,#1a2a3a 0%,transparent 60%),radial-gradient(800px 500px at 90% -10%,#2a1f3a 0%,transparent 60%),var(--bg);color:var(--text);min-height:100vh}
    header{padding:28px 28px 12px;display:flex;align-items:flex-end;justify-content:space-between;gap:16px;flex-wrap:wrap}
    h1{font-size:28px;letter-spacing:.2px}
    .sub{color:var(--muted);font-size:14px;margin-top:6px}
