<body>
    <header>
    <div>
      <h1>People Found - Select for Enrichment</h1>
      <div class="sub">All people found via SignalHire. AI-suggested contacts are highlighted. Select people to enrich with full contact details.</div>
    </div>
    <div class="stats">
      <div class="chip">${companiesSearched} companies</div>
      <div class="chip highlight">${totalPeople} people found</div>
      <div class="chip gold">${suggestedCount} AI suggested</div>
      <div class="chip ai">SignalHire + Claude AI</div>
    </div>
  </header>
  <div class="container">
    <div class="panel">
      <div class="toolbar">
        <div class="toolbar-left">
          <input id="search" type="text" placeholder="Filter by name, title, company, skills..."/>
          <label class="cb-wrap" style="gap:8px;font-size:13px;color:var(--muted)">
            <input type="checkbox" id="global-select-all"/>
            <span class="checkmark"></span>
            Select All
          </label>
        </div>
        <div style="display:flex;gap:10px;flex-wrap:wrap">
          <a class="btn btn-back" href="/webhook/jobs-ui">Back to Jobs</a>
          <a class="btn btn-secondary" href="/webhook/enriched-ui">View Enriched Data</a>
        </div>
      </div>
      <div class="select-bar">
        <span class="select-count"><span id="sel-count">0</span> people selected</span>
        <button class="btn btn-primary" id="enrich-btn" disabled>Enrich Selected</button>
        <span id="status-msg"></span>
      </div>
      <input type="hidden" id="batch-id" value="${searchBatchId}"/>
      <div id="people-list">
        ${peopleHtml}
      </div>
    </div>
  </div>
  <script>
    const allCbs = document.querySelectorAll('.person-cb');
    const companyCbs = document.querySelectorAll('.company-select-all');
    const globalCb = document.getElementById('global-select-all');
    const enrichBtn = document.getElementById('enrich-btn');
    const selCount = document.getElementById('sel-count');
    const statusMsg = document.getElementById('status-msg');
    const batchId = document.getElementById('batch-id').value;

    function updateCount() {
      const checked = document.querySelectorAll('.person-cb:checked').length;
      selCount.textContent = checked;
      enrichBtn.disabled = checked === 0;
    }

    allCbs.forEach(cb => cb.addEventListener('change', () => {
      updateCount();
      const comp = cb.dataset.company;
      const compCbs = document.querySelectorAll('.person-cb[data-company="'+comp+'"]');
      const compAll = document.querySelector('.company-select-all[data-company="'+comp+'"]');
      if(compAll) compAll.checked = [...compCbs].every(c => c.checked);
      globalCb.checked = [...allCbs].every(c => c.checked);
    }));

    companyCbs.forEach(cb => cb.addEventListener('change', () => {
      const comp = cb.dataset.company;
      const compCbs = document.querySelectorAll('.person-cb[data-company="'+comp+'"]');
      compCbs.forEach(c => c.checked = cb.checked);
      updateCount();
      globalCb.checked = [...allCbs].every(c => c.checked);
    }));

    globalCb.addEventListener('change', () => {
      allCbs.forEach(c => c.checked = globalCb.checked);
      companyCbs.forEach(c => c.checked = globalCb.checked);
      updateCount();
    });

    document.getElementById('search').addEventListener('input', function() {
      const q = this.value.toLowerCase();
      document.querySelectorAll('.company-section').forEach(sec => {
        const rows = sec.querySelectorAll('.person-row');
        let anyVisible = false;
        rows.forEach(r => {
          const show = !q || r.textContent.toLowerCase().includes(q);
          r.style.display = show ? '' : 'none';
          if (show) anyVisible = true;
        });
        sec.style.display = (!q || anyVisible || sec.querySelector('.company-header').textContent.toLowerCase().includes(q)) ? '' : 'none';
      });
    });

    enrichBtn.addEventListener('click', async () => {
      const selected = [...document.querySelectorAll('.person-cb:checked')].map(c => c.dataset.uid).filter(Boolean);
      if (selected.length === 0) return;
      enrichBtn.disabled = true;
      enrichBtn.textContent = 'Enriching...';
      statusMsg.textContent = 'Sending ' + selected.length + ' people for enrichment...';
      try {
        const resp = await fetch('/webhook/enrich-submit', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ uids: selected, searchBatchId: batchId })
        });
        const result = await resp.text();
        statusMsg.textContent = 'Enrichment request sent! Check back at View Enriched Data for results.';
        statusMsg.style.color = 'var(--green)';
        enrichBtn.textContent = 'Sent!';
      } catch(e) {
        statusMsg.textContent = 'Error: ' + e.message;
        statusMsg.style.color = '#f87171';
        enrichBtn.disabled = false;
        enrichBtn.textContent = 'Enrich Selected';
      }
    });
  </script>

\n  \n
</body>
</html>`;

return [{ json: { html, allPeople, searchBatchId, companiesSearched, totalPeople } }];

const items = $input.all().map(i => i.json);

// Group jobs by company
const companyMap = {};
for (const job of items) {
  const name = (job.employer_name || '').trim();
  if (!name) continue;
  if (!companyMap[name]) companyMap[name] = [];
  companyMap[name].push({
    title: job.job_title || '',
    location: job.job_location || '',
    type: job.job_employment_type || '',
    remote: job.job_is_remote ? 'Yes' : 'No'
  });
}

const companies = Object.keys(companyMap);
if (companies.length === 0) {
  return [{ json: { error: 'No companies found in selection' } }];
}

// Output one item per company for SignalHire search
return companies.map(name => ({
  json: {
    companyName: name,
    jobs: companyMap[name],
    companiesTotal: companies.length
  }
}));

