const shItems = $input.all();
const companyItems = $items('Prepare Company Search');

// Collect all profiles per company
const companyData = [];
for (let i = 0; i < shItems.length; i++) {
  const sh = shItems[i].json;
  const ci = companyItems[i] ? companyItems[i].json : {};
  const companyName = ci.companyName || 'Unknown';
  const jobs = ci.jobs || [];
  const profiles = sh.profiles || [];

  companyData.push({
    companyName,
    jobs,
    profiles: profiles.map(p => ({
      fullName: p.fullName || '',
      location: p.location || '',
      currentTitle: (p.experience && p.experience[0]) ? p.experience[0].title : '',
      currentCompany: (p.experience && p.experience[0]) ? p.experience[0].company : '',
      experience: (p.experience || []).slice(0, 4).map(e => e.title + ' at ' + e.company),
      skills: (p.skills || []).slice(0, 8),
      openToWork: p.openToWork || false,
      uid: p.uid || ''
    })),
    totalFound: sh.total || 0
  });
}

// Build prompt for Claude to pick best 3 per company
let promptText = 'You are a GTM sales intelligence assistant. Below are real people found at each company via SignalHire. For each company, analyze the people and pick EXACTLY 3 who are the best decision-makers to contact for a sales outreach related to the job postings shown.\n\nConsider:\n- Seniority and relevance of their role\n- How their department relates to the job posting\n- Their experience and skills\n\nReturn ONLY valid JSON in this format:\n{\n  "picks": [\n    {\n      "company": "Company Name",\n      "selected": [\n        { "fullName": "exact name from the list", "reason": "one sentence why" }\n      ]\n    }\n  ]\n}\n\n';

for (const cd of companyData) {
  promptText += '\n--- COMPANY: ' + cd.companyName + ' ---\n';
  promptText += 'Job postings: ' + cd.jobs.map(j => j.title + ' (' + j.location + ')').join(', ') + '\n';
  promptText += 'People found (' + cd.profiles.length + '):\n';
  for (const p of cd.profiles) {
    promptText += '  â€¢ ' + p.fullName + ' | ' + p.currentTitle + ' | ' + p.location + ' | Skills: ' + p.skills.slice(0, 5).join(', ') + '\n';
  }
  if (cd.profiles.length === 0) {
    promptText += '  (No senior leaders found)\n';
  }
}

return [{ json: {
  prompt: promptText,
  companyData: companyData,
  companiesTotal: companyData.length
} }];

const claudeResp = $input.all()[0].json;
const analysisData = $items('Prepare AI Analysis')[0].json;
const companyData = analysisData.companyData || [];
const companiesTotal = analysisData.companiesTotal || 0;

// Parse Claude response
let text = '';
if (claudeResp.content && Array.isArray(claudeResp.content) && claudeResp.content[0]) {
  text = claudeResp.content[0].text || '';
}

let aiPicks;
try {
  aiPicks = JSON.parse(text);
} catch(e) {
  const m = text.match(/\{[\s\S]*\}/);
  if (m) { try { aiPicks = JSON.parse(m[0]); } catch(e2) {} }
}

const searchBatchId = 'batch_' + Date.now() + '_' + Math.random().toString(36).substr(2, 6);
const allPeople = [];

// Build a map of AI picks per company
const picksMap = {};
if (aiPicks && aiPicks.picks) {
  for (const pick of aiPicks.picks) {
    const compName = (pick.company || '').toLowerCase();
    picksMap[compName] = {};
    for (const sel of (pick.selected || [])) {
      picksMap[compName][(sel.fullName || '').toLowerCase()] = sel.reason || '';
    }
  }
}

// Process ALL profiles from ALL companies
for (const cd of companyData) {
  const companyName = cd.companyName || 'Unknown';
  const sourceJob = cd.jobs && cd.jobs[0] ? cd.jobs[0] : {};
  const compKey = companyName.toLowerCase();
  const compPicks = picksMap[compKey] || {};

  // Also try partial matching for company names
  let matchedPicks = compPicks;
  if (Object.keys(compPicks).length === 0) {
    for (const pk of Object.keys(picksMap)) {
      if (compKey.includes(pk) || pk.includes(compKey)) {
        matchedPicks = picksMap[pk];
        break;
      }
    }
  }

  for (const profile of (cd.profiles || [])) {
    const nameKey = (profile.fullName || '').toLowerCase();
    const isSuggested = nameKey in matchedPicks;
    const reason = matchedPicks[nameKey] || '';

    allPeople.push({
      company: companyName,
      fullName: profile.fullName || 'Unknown',
      currentTitle: profile.currentTitle || 'N/A',
      location: profile.location || 'N/A',
      skills: profile.skills || [],
      experience: profile.experience || [],
      openToWork: profile.openToWork || false,
      uid: profile.uid || '',
      is_suggested: isSuggested,
      suggestion_reason: reason,
      sourceJobTitle: sourceJob.title || '',
      sourceJobLocation: sourceJob.location || ''
    });
  }
}

return [{ json: {
  allPeople,
  companiesSearched: companiesTotal,
  totalPeople: allPeople.length,
  searchBatchId
}}];

const data = $json;
const allPeople = data.allPeople || [];
const batchId = data.searchBatchId || '';

if (allPeople.length === 0) {
  return [{ json: { ...data, sqlStatements: [], error: 'No people to save' } }];
}

const createTableSQL = `CREATE TABLE IF NOT EXISTS people (
  id INT AUTO_INCREMENT PRIMARY KEY,
  uid VARCHAR(64) NOT NULL,
  company_name VARCHAR(255),
  full_name VARCHAR(255),
  current_title VARCHAR(500),
  location VARCHAR(255),
  skills TEXT,
  experience TEXT,
  open_to_work TINYINT DEFAULT 0,
  is_suggested TINYINT DEFAULT 0,
  suggestion_reason TEXT,
  source_job_title VARCHAR(500),
  source_job_location VARCHAR(255),
  search_batch_id VARCHAR(64),
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  UNIQUE KEY unique_uid (uid)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4`;

const esc = (s) => String(s || '').replace(/\\/g, '\\\\').replace(/'/g, "\\'");

const values = allPeople.map(p => {
  return `('${esc(p.uid)}', '${esc(p.company)}', '${esc(p.fullName)}', '${esc(p.currentTitle)}', '${esc(p.location)}', '${esc(JSON.stringify(p.skills))}', '${esc(JSON.stringify(p.experience))}', ${p.openToWork ? 1 : 0}, ${p.is_suggested ? 1 : 0}, '${esc(p.suggestion_reason)}', '${esc(p.sourceJobTitle)}', '${esc(p.sourceJobLocation)}', '${esc(batchId)}')`;
}).join(',\n');

const insertSQL = `INSERT INTO people (uid, company_name, full_name, current_title, location, skills, experience, open_to_work, is_suggested, suggestion_reason, source_job_title, source_job_location, search_batch_id)
VALUES ${values}
ON DUPLICATE KEY UPDATE
  company_name = VALUES(company_name),
  full_name = VALUES(full_name),
  current_title = VALUES(current_title),
  location = VALUES(location),
  skills = VALUES(skills),
  experience = VALUES(experience),
  open_to_work = VALUES(open_to_work),
  is_suggested = VALUES(is_suggested),
  suggestion_reason = VALUES(suggestion_reason),
  source_job_title = VALUES(source_job_title),
  source_job_location = VALUES(source_job_location),
  search_batch_id = VALUES(search_batch_id)`;

return [{ json: { ...data, createTableSQL, insertSQL } }];

const payload = $json.body || $json;
const uids = payload.uids || [];
const searchBatchId = payload.searchBatchId || '';

if (!Array.isArray(uids) || uids.length === 0) {
  return [{ json: { error: 'No UIDs provided', uids: [], searchBatchId } }];
}

// Use WEBHOOK_URL env var (set by start_render.js) or fallback to localhost
const baseUrl = process.env.WEBHOOK_URL ? process.env.WEBHOOK_URL.replace(/\/$/, '') : 'http://localhost:5678';
const callbackUrl = baseUrl + '/webhook/signalhire-callback';

return [{ json: {
  items: uids,
  callbackUrl: callbackUrl,
  searchBatchId: searchBatchId,
  totalRequested: uids.length
} }];

const callbackData = $json.body || $json;
const items = Array.isArray(callbackData) ? callbackData : (callbackData.items || callbackData.data || [callbackData]);

if (!items || items.length === 0) {
  return [{ json: { error: 'No callback data received', insertSQL: 'SELECT 1' } }];
}

const esc = (s) => String(s || '').replace(/\\/g, '\\\\').replace(/'/g, "\\'");
const jsonEsc = (v) => esc(JSON.stringify(v || []));

const successItems = items.filter(i => i.status === 'success' && i.candidate);

if (successItems.length === 0) {
  const failInfo = items.map(i => i.status + ': ' + (i.item || '')).join(', ');
  return [{ json: { error: 'No successful enrichments. Statuses: ' + failInfo, insertSQL: 'SELECT 1', totalProcessed: 0 } }];
}

const values = successItems.map(item => {
  const c = item.candidate;

  const emails = (c.contacts || []).filter(ct => ct.type === 'email').map(ct => ({
    value: ct.value || '', subType: ct.subType || 'unknown', rating: ct.rating || '0'
  }));

  const phones = (c.contacts || []).filter(ct => ct.type === 'phone').map(ct => ({
    value: ct.value || '', subType: ct.subType || 'unknown', rating: ct.rating || '0'
  }));

  const socialLinks = (c.social || []).map(s => ({
    type: s.type || '', link: s.link || '', rating: s.rating || '0'
  }));

  const education = (c.education || []).map(ed => ({
    university: ed.university || '', faculty: ed.faculty || '',
    degree: Array.isArray(ed.degree) ? ed.degree.join(', ') : (ed.degree || ''),
    startedYear: ed.startedYear || '', endedYear: ed.endedYear || ''
  }));

  const experience = (c.experience || []).map(exp => ({
    position: exp.position || '', company: exp.company || '',
    location: exp.location || '', current: exp.current || false,
    started: exp.started || '', ended: exp.ended || '',
    summary: exp.summary || '', companySize: exp.companySize || '',
    staffCount: exp.staffCount || 0, industry: exp.industry || '',
    website: exp.website || ''
  }));

  const languages = (c.language || []).map(l => ({
    name: l.name || '', proficiency: l.proficiency || ''
  }));

  const certifications = (c.certification || []).map(ct => ({
    name: ct.name || '', authority: ct.authority || '', license: ct.license || ''
  }));

  const loc = (c.locations || [])[0] || {};
  const currentExp = (c.experience || []).find(e => e.current) || (c.experience || [])[0] || {};
  const companyName = currentExp.company || '';

  return "  ('" + esc(c.uid || '') + "', '" + esc(c.fullName || '') + "', '" + esc(companyName) + "', '" + esc(c.headLine || '') + "', '" + esc(c.summary || '') + "', '" + esc(c.photo ? c.photo.url : '') + "', '" + esc(loc.name || '') + "', '" + jsonEsc(emails) + "', '" + jsonEsc(phones) + "', '" + jsonEsc(socialLinks) + "', '" + jsonEsc(education) + "', '" + jsonEsc(experience) + "', '" + jsonEsc(languages) + "', '" + jsonEsc(certifications) + "', '" + jsonEsc(c.skills || []) + "', " + (c.openToWork ? 1 : 0) + ", 'success', '')";
}).join(',\n');

const insertSQL = "INSERT INTO enriched_people (uid, full_name, company_name, headline, summary, photo_url, location_name, emails, phones, social_links, education, experience_detailed, languages, certifications, skills, open_to_work, enrichment_status, search_batch_id) VALUES\n" + values + "\nON DUPLICATE KEY UPDATE full_name=VALUES(full_name), company_name=VALUES(company_name), headline=VALUES(headline), summary=VALUES(summary), photo_url=VALUES(photo_url), location_name=VALUES(location_name), emails=VALUES(emails), phones=VALUES(phones), social_links=VALUES(social_links), education=VALUES(education), experience_detailed=VALUES(experience_detailed), languages=VALUES(languages), certifications=VALUES(certifications), skills=VALUES(skills), enrichment_status=VALUES(enrichment_status), enriched_at=CURRENT_TIMESTAMP";

return [{ json: { insertSQL, totalProcessed: successItems.length, failedItems: items.filter(i => i.status !== 'success').map(i => ({ item: i.item, status: i.status })) } }];

const items = $input.all().map(i => i.json);

const esc = (s) => String(s || '').replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;').replace(/"/g,'&quot;');

const tryParse = (v) => {
  if (!v) return [];
  if (Array.isArray(v)) return v;
  if (typeof v === 'object' && v !== null) return Array.isArray(v) ? v : [v];
  try { const parsed = JSON.parse(v); return Array.isArray(parsed) ? parsed : [parsed]; } catch(e) { return []; }
};

// Filter out rows that have no real data (empty MySQL results produce one row with nulls)
const realItems = items.filter(p => p.uid && p.uid !== '' && p.uid !== null);

// Group by company
const grouped = {};
for (const p of realItems) {
  const company = p.display_company || p.company_name || 'Unknown Company';
  if (!grouped[company]) grouped[company] = [];
  grouped[company].push(p);
}

const companies = Object.keys(grouped);
const totalPeople = realItems.length;

let cardsHtml = '';
if (totalPeople === 0) {
  cardsHtml = '<div class="empty-state"><div class="empty-icon">&#128269;</div><h3>No enriched data yet</h3><p>Select people from the Leaders page and click \"Enrich Selected\" to fetch their contact details via SignalHire Person API.</p><p class="note">Note: The Person API uses a callback mechanism. After you submit enrichment requests, SignalHire processes them asynchronously and sends results back to your callback webhook. This may take a few seconds to a few minutes.</p></div>';
} else {
  for (const company of companies) {
    const people = grouped[company];
    cardsHtml += '<div class="company-group">';
    cardsHtml += '<div class="company-group-header">' + esc(company) + ' (' + people.length + ' people)</div>';

    for (const p of people) {
      const emails = tryParse(p.emails);
      const phones = tryParse(p.phones);
      const socialLinks = tryParse(p.social_links);
      const education = tryParse(p.education);
      const experience = tryParse(p.experience_detailed);
      const languages = tryParse(p.languages);
      const certifications = tryParse(p.certifications);
      const skills = tryParse(p.skills);

      const statusClass = p.enrichment_status === 'success' ? 'status-success' : (p.enrichment_status === 'failed' ? 'status-failed' : 'status-pending');

      let emailsHtml = '';
      if (emails.length > 0) {
        emailsHtml = emails.map(e => '<div class="contact-item"><span class="contact-badge">' + esc(e.subType || e.type || 'email') + '</span><a href="mailto:' + esc(e.value) + '" class="contact-value">' + esc(e.value) + '</a>' + (e.rating && e.rating !== '0' ? '<span class="rating">&#9733; ' + e.rating + '%</span>' : '') + '</div>').join('');
      } else {
        emailsHtml = '<div class="no-data">No emails found</div>';
      }

      let phonesHtml = '';
      if (phones.length > 0) {
        phonesHtml = phones.map(ph => '<div class="contact-item"><span class="contact-badge">' + esc(ph.subType || ph.type || 'phone') + '</span><a href="tel:' + esc(ph.value) + '" class="contact-value">' + esc(ph.value) + '</a>' + (ph.rating && ph.rating !== '0' ? '<span class="rating">&#9733; ' + ph.rating + '%</span>' : '') + '</div>').join('');
      } else {
        phonesHtml = '<div class="no-data">No phone numbers found</div>';
      }

      let socialHtml = '';
      if (Array.isArray(socialLinks) && socialLinks.length > 0) {
        socialHtml = socialLinks.map(s => {
          const label = s.type === 'li' ? 'LinkedIn' : (s.type === 'fb' ? 'Facebook' : (s.type === 'tw' ? 'Twitter' : s.type));
          return '<a class="social-link" href="' + esc(s.link) + '" target="_blank">' + esc(label) + '</a>';
        }).join(' ');
      }

      let eduHtml = '';
      if (education.length > 0) {
        eduHtml = education.map(ed => '<div class="edu-item"><strong>' + esc(ed.university || ed.school || '') + '</strong>' + (ed.degree ? ' - ' + esc(ed.degree) : '') + (ed.faculty ? ' (' + esc(ed.faculty) + ')' : '') + (ed.startedYear || ed.endedYear ? '<span class="date-range"> ' + (ed.startedYear || '?') + ' - ' + (ed.endedYear || 'Present') + '</span>' : '') + '</div>').join('');
      }

      let expHtml = '';
      if (experience.length > 0) {
        expHtml = experience.slice(0, 5).map(exp => {
          let dateStr = '';
          if (exp.started) {
            const start = exp.started.substring(0, 7);
            const end = exp.current ? 'Present' : (exp.ended ? exp.ended.substring(0, 7) : '');
            dateStr = ' (' + start + (end ? ' - ' + end : '') + ')';
          }
          return '<div class="exp-item"><div class="exp-title">' + esc(exp.position || '') + '</div><div class="exp-company">' + esc(exp.company || '') + dateStr + '</div>' + (exp.industry ? '<span class="exp-tag">' + esc(exp.industry) + '</span>' : '') + (exp.companySize ? '<span class="exp-tag">' + esc(exp.companySize) + ' employees</span>' : '') + (exp.summary ? '<div class="exp-summary">' + esc(exp.summary).substring(0, 200) + '</div>' : '') + '</div>';
        }).join('');
      }

      let skillsHtml = '';
      if (Array.isArray(skills) && skills.length > 0) {
        skillsHtml = skills.slice(0, 12).map(s => '<span class="skill-chip">' + esc(typeof s === 'string' ? s : s.name || '') + '</span>').join('');
      }

      let langHtml = '';
      if (languages.length > 0) {
        langHtml = languages.map(l => '<span class="lang-chip">' + esc(l.name || '') + (l.proficiency ? ' (' + esc(l.proficiency) + ')' : '') + '</span>').join(' ');
      }

      let certHtml = '';
      if (certifications.length > 0) {
        certHtml = certifications.map(ct => '<div class="cert-item">' + esc(ct.name || '') + (ct.authority ? ' - ' + esc(ct.authority) : '') + '</div>').join('');
      }

      const photoHtml = p.photo_url ? '<img src="' + esc(p.photo_url) + '" class="person-photo" onerror="this.style.display=\'none\';this.nextElementSibling.style.display=\'flex\'"/><div class="photo-placeholder" style="display:none">&#128100;</div>' : '<div class="photo-placeholder">&#128100;</div>';

      cardsHtml += '<div class="person-card">';
      cardsHtml += '<div class="person-header">';
      cardsHtml += '<div class="person-photo-wrap">' + photoHtml + '</div>';
      cardsHtml += '<div class="person-info"><h3>' + esc(p.full_name || 'Unknown') + '</h3>';
      if (p.headline) cardsHtml += '<div class="person-headline">' + esc(p.headline) + '</div>';
      if (p.location_name) cardsHtml += '<div class="person-location">&#128205; ' + esc(p.location_name) + '</div>';
      cardsHtml += '<span class="status-badge ' + statusClass + '">' + esc(p.enrichment_status || 'unknown') + '</span>';
      cardsHtml += '</div></div>';

      if (p.summary) {
        cardsHtml += '<div class="person-summary">' + esc(p.summary).substring(0, 400) + '</div>';
      }

      cardsHtml += '<div class="section"><div class="section-title">&#9993; Contact</div>';
      cardsHtml += '<div class="subsection"><strong>Emails</strong>' + emailsHtml + '</div>';
      cardsHtml += '<div class="subsection"><strong>Phones</strong>' + phonesHtml + '</div>';
      if (socialHtml) cardsHtml += '<div class="subsection"><strong>Social</strong><div class="social-links">' + socialHtml + '</div></div>';
      cardsHtml += '</div>';

      if (skillsHtml) {
        cardsHtml += '<div class="section"><div class="section-title">&#127919; Skills</div><div class="skills-wrap">' + skillsHtml + '</div></div>';
      }

      if (expHtml) {
        cardsHtml += '<div class="section"><div class="section-title">&#128188; Experience</div>' + expHtml + '</div>';
      }

      if (eduHtml) {
        cardsHtml += '<div class="section"><div class="section-title">&#127891; Education</div>' + eduHtml + '</div>';
      }

      if (langHtml) {
        cardsHtml += '<div class="section"><div class="section-title">&#127760; Languages</div><div class="lang-wrap">' + langHtml + '</div></div>';
      }

      if (certHtml) {
        cardsHtml += '<div class="section"><div class="section-title">&#127942; Certifications</div>' + certHtml + '</div>';
      }

      cardsHtml += '</div>'; // person-card
    }
    cardsHtml += '</div>'; // company-group
  }
}

