const html = `<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Login - GTM Console</title>
  <script src="https://accounts.google.com/gsi/client" async defer></script>
  <style>
    :root { --bg: #0f1220; --card: #171a2b; --text: #eef1ff; --accent: #f4b266; }
    body { background: var(--bg); color: var(--text); font-family: sans-serif; display: flex; align-items: center; justify-content: center; height: 100vh; margin: 0; }
    .login-box { background: var(--card); padding: 40px; border-radius: 12px; text-align: center; box-shadow: 0 10px 30px rgba(0,0,0,0.5); width: 100%; max-width: 400px; }
    h1 { margin-bottom: 8px; font-size: 24px; }
    p { color: #9aa4bf; margin-bottom: 24px; font-size: 14px; }
    .g_id_signin { display: flex; justify-content: center; }
    #error-msg { color: #ff6b6b; margin-top: 16px; font-size: 13px; display: none; }
  </style>
</head>
<body>
  <div class="login-box">
    <h1>GTM Console</h1>
    <p>Sign in to access the dashboard</p>
    <div id="g_id_onload"
         data-client_id="447046445190-al88jt66ddfi8qe18v9m4n3bkticfqbf.apps.googleusercontent.com"
         data-callback="handleCredentialResponse"
         data-auto_select="false"
         data-itp_support="true">
    </div>
    <div class="g_id_signin" data-type="standard" data-size="large" data-theme="filled_black" data-text="sign_in_with" data-shape="rectangular" data-logo_alignment="left"></div>
    <div id="error-msg"></div>
  </div>
  <script>
    function handleCredentialResponse(response) {
      const responsePayload = decodeJwtResponse(response.credential);
      const email = responsePayload.email;
      const domain = email.split('@')[1];
      const allowed = ['insightstap.com', 'sdtcdigital.com'];

      if (allowed.includes(domain)) {
        localStorage.setItem('gtm_user', JSON.stringify({ email: email, name: responsePayload.name, picture: responsePayload.picture }));
        const redirect = new URLSearchParams(window.location.search).get('returnUrl') || '/webhook/jobs-ui';
        window.location.href = redirect;
      } else {
        document.getElementById('error-msg').style.display = 'block';
        document.getElementById('error-msg').textContent = 'Access Denied: @' + domain + ' is not authorized.';
      }
    }
    function decodeJwtResponse(token) {
      var base64Url = token.split('.')[1];
      var base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      var jsonPayload = decodeURIComponent(window.atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
      return JSON.parse(jsonPayload);
    }
  </script>
</body>
</html>`;
return [{ json: { html } }];
